import { React, Component } from "react";
import styled from "styled-components";
import "./App.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import { faMinus } from "@fortawesome/free-solid-svg-icons";
import { Home, Title, Content, Counter, Button, Form } from "./Style";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { value: 0 };
    this.buttonAdd = this.buttonAdd.bind(this);
    this.buttonSub = this.buttonSub.bind(this);
  }
  buttonAdd(event) {
    this.setState({ value: this.state.value + 1 });
  }

  buttonSub(event) {
    // console.log("this.state.value", this.state > 0);
    // console.log("this.state.value", this.state);

    if (this.state.value > 0) {
      this.setState({ value: this.state.value - 1 });
    }
  }

  render() {
    return (
      <div>
        <Title> Billu barber and salon</Title>
        <Home>
          <Content>
            <Form>
              <label>
                No of barbers :
                <input type="text" name="name" style={{ border: "none" }} />
              </label>
              <input type="submit" value="Okay" style={{ border: "none" }} />
            </Form>
            <Counter>
              <h3> People in line : {this.state.value} </h3>
              <Button>
                <FontAwesomeIcon
                  onClick={this.buttonAdd}
                  icon={faPlus}
                  style={{
                    padding: "10px 20px",
                    backgroundColor: "d5cfbd",
                    color: "7a6a53",
                  }}
                />
                <br />
                <FontAwesomeIcon
                  onClick={this.buttonSub}
                  icon={faMinus}
                  style={{
                    padding: "10px 20px",
                    backgroundColor: "d5cfbd",
                    color: "7a6a53",
                  }}
                />
              </Button>
            </Counter>
            <h3> Your count : {this.state.value + 1}</h3>
            <h3> Ajha parkhine time : {this.state.value * 18} minutes </h3>
          </Content>
          {/* <div> Contact No : </div> */}
        </Home>
      </div>
    );
  }
}

export default App;
