import styled from "styled-components";

export const Home = styled.section`
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  position: absolute;
  border-radius: 20px;
`;

export const Title = styled.section`
  font-size: 2.8rem;
  font-weight: 640;
  text-align: center;
  margin-top: 270px;
  color: #7a6a53;
`;

export const Content = styled.section`
  margin-top: 40px;
  font-size: 1.1rem;
  text-align: center;
  // color: #7a6a53;
  color: #948c75;
`;

