import { React, Component } from "react";
import styled from "styled-components";
import "./App.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import { faMinus } from "@fortawesome/free-solid-svg-icons";

const Home = styled.section`
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  position: absolute;
  border-radius: 20px;
`;

const Title = styled.section`
  font-size: 2.8rem;
  font-weight: 640;
  text-align: center;
  margin-top: 270px;
  color: #7a6a53;
`;

const Content = styled.section`
  margin-top: 40px;
  font-size: 1.1rem;
  text-align: center;
  // color: #7a6a53;
  color: #948c75;
`;

const Counter = styled.section``;

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { value: 0 };
    this.buttonAdd = this.buttonAdd.bind(this);
    this.buttonSub = this.buttonSub.bind(this);
  }
  buttonAdd(event) {
    this.setState({ value: this.state.value + 1 });
  }

  buttonSub(event) {
    // do {
    //   this.setState({ value: this.state.value - 1 });
    // } while (this.state < 0);

    if (this.state <= 0) {
      this.setState({ value: this.state.value - 1 });
    }
  }

  render() {
    return (
      <div>
        <Title> Billu barber and salon</Title>
        <Home>
          {" "}
          <Content>
            <Counter>
              <h3> People in line : {this.state.value} </h3>
              {/* <button onClick={this.buttonClicked}> click </button> */}
              <FontAwesomeIcon onClick={this.buttonAdd} icon={faPlus} />
              <br />
              <FontAwesomeIcon onClick={this.buttonSub} icon={faMinus} />
            </Counter>

            <h3> Your count : {}</h3>
            <h3> Ajha parkhine time : </h3>
          </Content>
          {/* <div> Contact No : </div> */}
        </Home>
      </div>
    );
  }
}

export default App;
